<div class="content-after-header">
    <table class="child-table">
        <tbody>
            <tr>
                <th colspan="2">1. Informações</th>
            </tr>
            <tr>
                <td>Autor</td>
                <td><?= $formInfo['author'] ?></td>
            </tr>
            <tr>
                <td>Formulário</td>
                <td><?= $formInfo['name'] ?></td>
            </tr>
            <tr>
                <td>Descrição</td>
                <td><?= $formInfo['description'] ?></td>
            </tr>
            <tr>
                <td>Data Inicial</td>
                <td><?= formatDate($formInfo['starts']) ?></td>
            </tr>
            <tr>
                <td>Data Final</td>
                <td><?= formatDate($formInfo['ends']) ?></td>
            </tr>
        </tbody>
    </table>

    <?php foreach ($formulario as $formData) { ?>
        <table class="child-table">
            <tbody>
                <tr>
                    <th colspan="3"><?= $i ?>. <?= $formData['question_text'] ?></th>
                </tr>

                <?php
                $hasOptions = false;
                $totalVotos = 0;
                $alternativaCorreta = '';

                // Calcular o total de votos para esta pergunta
                foreach ($formData['options'] as $formRespose) {
                    $totalVotos += $formRespose['option_count'];
                    if (!empty(trim($formRespose['option_text']))) {
                        $hasOptions = true;
                    }
                    // Verificar se a opção é a correta
                    if ($formRespose['option_true'] == 1) {
                        $alternativaCorreta = $formRespose['option_text'];
                    }
                }

                if ($hasOptions) { ?>
                    <tr>
                        <th>Opção</th>
                        <th>Quantidade de respostas</th>
                        <th>Porcentagem</th>
                    </tr>
                <?php } ?>

                <?php foreach ($formData['options'] as $formRespose) { ?>
                    <?php if (empty(trim($formRespose['option_text']))) { ?>
                        <tr>
                            <td colspan="3">Campo texto</td>
                        </tr>
                    <?php } else {
                        // Calcular a porcentagem
                        $porcentagem = ($totalVotos > 0) ? ($formRespose['option_count'] / $totalVotos) * 100 : 0;
                    ?>
                        <tr>
                            <td><?= $formRespose['option_text'] ?></td>
                            <td><?= $formRespose['option_count'] ?></td>
                            <td><?= number_format($porcentagem, 2) ?>%</td>
                        </tr>
                    <?php } ?>
                <?php } ?>

                <?php if (!empty($alternativaCorreta)) { ?>
                    <tr>
                        <th colspan="3">A alternativa correta é: <?= $alternativaCorreta ?></th>
                    </tr>
                <?php } ?>
            </tbody>
        </table>

        <?php $i++ ?>
    <?php } ?>
</div>